<?phpnamespace App\Repository;use App\Entities\AuctionAgreement;use Doctrine\ORM\EntityManager;use Doctrine\ORM\EntityRepository;use Doctrine\ORM\Query;class AuctionAgreementRepository extends EntityRepository {    private $class = 'App\Entities\AuctionAgreement';    private $em;    public function __construct(EntityManager $em) {        $this->em = $em;    }    public function prepareData($data) {        return new AuctionAgreement($data);    }    public function create(AuctionAgreement $option) {        $this->em->persist($option);        $this->em->flush();        return $option;    }    public function update(AuctionAgreement $option, $data) {        if (isset($data['is_form_filled'])) {            $option->setIs_form_filled($data['is_form_filled']);        }        if (isset($data['signature'])) {            $option->setSignature($data['signature']);        }        if (isset($data['data_json'])) {            $option->setData_json($data['data_json']);        }        if (isset($data['pdf'])) {            $option->setPdf($data['pdf']);        }        if (isset($data['fillup_date'])) {            $option->setFillup_date($data['fillup_date']);        }        $this->em->persist($option);        $this->em->flush();    }    public function ofId($id) {        return $this->em->getRepository($this->class)->findOneBy([                    'id' => $id        ]);    }}